waitTimeInSeconds.value:
@max(1,
    div(
        sub(
            ticks(concat(substring(convertFromUtc(utcNow(), 'Tokyo Standard Time', 's'), 0, 11), pipeline().parameters.group3StartTime, ':00Z')),
            ticks(convertFromUtc(utcNow(), 'Tokyo Standard Time', 's'))
        ),
        10000000
    )
)

